package com.jiagu.mobile.zhifu;/** * @ClassName: Result * @Description: 返回结果* @author zz* @date 2015年1月15日 下午12:27:33 * @version 1* @company 西安甲骨企业文化传播有限公司  */ public class Result {	String resultStatus;	String result;	String memo;	public Result(String rawResult) {		try {			String[] resultParams = rawResult.split(";");			for (String resultParam : resultParams) {				if (resultParam.startsWith("resultStatus")) {					resultStatus = gatValue(resultParam, "resultStatus");				}				if (resultParam.startsWith("result")) {					result = gatValue(resultParam, "result");				}				if (resultParam.startsWith("memo")) {					memo = gatValue(resultParam, "memo");				}			}		} catch (Exception e) {			e.printStackTrace();		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}}